const $navbar=document.querySelector(".navbar");function showHideNav(){window.scrollY>50?$navbar.classList.add("navbar-white"):$navbar.classList.remove("navbar-white")}window.addEventListener("scroll",showHideNav);const $buttons=document.querySelectorAll("button[data-toggle]"),$headers=document.querySelectorAll(".card-header");function resetHeaders(){$headers.forEach(header=>header.classList.remove("card-active"))}function handleClick(){header=this.parentElement.parentElement,header.classList.contains("card-active")||resetHeaders(),header.classList.toggle("card-active")}function initMap(){let options={zoom:10,center:{lat:40.733837,lng:-73.584152}},map=new google.maps.Map(document.getElementById("map"),options),marker=new google.maps.Marker({position:{lat:40.733837,lng:-73.584152},map:map}),infoWindow=new google.maps.InfoWindow({content:'<p class="text-dark p-0 m-0">4130 Turkey Pen Road</p>'});marker.addListener("click",function(){infoWindow.open(map,marker)})}$buttons.forEach(button=>button.addEventListener("click",handleClick));